import React, { Component } from 'react';
import validator from 'validator';
import { v4 as uuidv4 } from 'uuid';
class ContactForm extends Component {
  state = {
    firstName: '',
    lastName: '',
    email: '',
    profession: '',
    selectedValue: 'personal',
    errors: {},
  };
  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  handleSubmit = (e) => {
    e.preventDefault();
    const { firstName, lastName, email, profession } = this.state;
    if (firstName === '') {
      this.setState({
        errors: {
          firstName: 'please provide valid name',
        },
      });
      return;
    }
    if (lastName === '') {
      this.setState({
        errors: {
          lastName: 'please provide valid last Name',
        },
      });
      return;
    }
    if (email === '' || !validator.isEmail(email)) {
      this.setState({
        errors: {
          email: 'please provide valid email address',
        },
      });
      return;
    }
    if (profession === '') {
      this.setState({
        errors: {
          profession: 'please provide valid profession',
        },
      });
      return;
    } else {
      this.setState({
        errors: {
          profession: '',
        },
      });
    }
    this.props.addContact({ id: uuidv4(), ...this.state });
  };
  render() {
    const {
      firstName,
      lastName,
      email,
      profession,
      selectedValue,
      errors,
    } = this.state;
    return (
      <React.Fragment>
        <h3>Contact Form</h3>
        <form onSubmit={this.handleSubmit}>
          <div className='input-field'>
            <input
              type='text'
              value={firstName}
              onChange={this.handleChange}
              name='firstName'
              className='firstName'
            />
            <label htmlFor='firstName'>First Name</label>
            <span className='helper-text red-text'>
              {errors.firstName || errors.firstName}
            </span>
          </div>
          <div className='input-field'>
            <input
              type='text'
              value={lastName}
              name='lastName'
              onChange={this.handleChange}
              className='lastName'
            />
            <label htmlFor='lastName'>Last Name</label>
            <span className='helper-text red-text'>
              {errors.lastName || errors.lastName}
            </span>
          </div>
          <div className='input-field'>
            <input
              type='text'
              value={email}
              name='email'
              onChange={this.handleChange}
              className='email'
            />
            <label htmlFor='email'>email</label>
            <span className='helper-text red-text'>
              {errors.email || errors.email}
            </span>
          </div>
          <div className='input-field'>
            <input
              type='text'
              value={profession}
              name='profession'
              onChange={this.handleChange}
              className='profession'
            />
            <label htmlFor='profession'>Profession</label>
            <span className='helper-text red-text'>
              {errors.profession || errors.profession}
            </span>
          </div>
          <p>
            <label>
              <input
                name='selectedValue'
                type='radio'
                checked={selectedValue === 'personal'}
                value='personal'
                onChange={this.handleChange}
              />
              <span>Personal</span>
            </label>
            <label>
              <input
                name='selectedValue'
                type='radio'
                checked={selectedValue === 'professional'}
                value='professional'
                onChange={this.handleChange}
              />
              <span>Professional</span>
            </label>
          </p>
          <button type='submit' className='btn waves-effect waves-light'>
            Submit
          </button>
        </form>
      </React.Fragment>
    );
  }
}

export default ContactForm;
